document.addEventListener("DOMContentLoaded",(function(){var t=document.getElementById("flightInfo");chrome.storage.local.get("flightData",(function(n){if(n.flightData){var a=n.flightData,e=a.from,o=a.to,c=a.distance,i=a.co2;t.innerHTML="\n          <div>✈️ ".concat(e," → ").concat(o,"</div>\n          <div>🛫 Distance: ").concat(c,' km</div>\n          <div class="co2">🌱 CO₂: ').concat(i," kg</div>\n        ")}else t.textContent="No flight data available."}))}));